mapper.sql:
WHERE 1=1

    and id = #{id,jdbcType=INTEGER}


    and plateform = #{plateform,jdbcType=INTEGER}


    and data_type = #{dataType,jdbcType=INTEGER}


    and page = #{page,jdbcType=VARCHAR}


    and module = #{module,jdbcType=VARCHAR}


    and position = #{position,jdbcType=VARCHAR}


    and title_name like CONCAT('%', #{titleName,jdbcType=VARCHAR},'%')


    and product_id = #{productId,jdbcType=VARCHAR}


    and city_id = #{cityId,jdbcType=INTEGER}


    and city_name like CONCAT('%', #{cityName,jdbcType=VARCHAR},'%')


    and states = #{states,jdbcType=TINYINT}


    and start_time >= DATE_FORMAT(#{startTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H-%i-%s')


    and end_time <= DATE_FORMAT(#{endTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H-%i-%s')

mapper.select:

			select count(*) from dj_advert
			where (states = 0 or states = 1)
			and data_type = #{dataType,jdbcType=TINYINT}
			and module = #{module,jdbcType=VARCHAR}
			and position = #{position,jdbcType=VARCHAR}
			and ( ((start_time >= #{startTime,jdbcType=DATE}) and (start_time <= #{endTime,jdbcType=DATE}))
			or ((end_time >= #{startTime,jdbcType=DATE}) and (end_time <= #{endTime,jdbcType=DATE}))
			or ((start_time >= #{startTime,jdbcType=DATE}) and (end_time <= #{endTime,jdbcType=DATE}))
			or ((start_time <= #{startTime,jdbcType=DATE}) and (end_time >= #{endTime,jdbcType=DATE})) )


            and id != #{id,jdbcType=INTEGER}


            and city_id = #{cityId,jdbcType=INTEGER}
