mapper.select:
select

from dj_order_status_log where order_id=#{orderId}
order by record_time

SELECT
FROM dj_order_status_log WHERE id IN ( SELECT MAX(id) FROM dj_order_status_log WHERE 1 = 1
and order_id = #{orderId,jdbcType=VARCHAR}
and order_type = #{orderType,jdbcType=VARCHAR}
and is_show = #{isShow,jdbcType=BIT}
GROUP BY order_status ) ORDER BY record_time
